window.addEventListener("DOMContentLoaded",()=>{const e=new IntersectionObserver(e=>{e.forEach(e=>{const t=e.target.getAttribute("id");e.intersectionRatio>0?document.querySelector(`nav li a[href="#${t}"]`).parentElement.classList.add("active"):document.querySelector(`nav li a[href="#${t}"]`).parentElement.classList.remove("active")})});document.querySelectorAll("section[id]").forEach(t=>{e.observe(t)})}),document.addEventListener("DOMContentLoaded",()=>{const e=window.revealer({revealElementSelector:".nav-js",options:{anchorSelector:".nav-btn-js"}}),t=document.querySelector(".nav-btn-js");document.querySelectorAll(".header__menu-item-link").forEach(n=>{n.addEventListener("click",()=>{e.hide(),t.setAttribute("data-open",!1)})}),t.addEventListener("click",()=>{e.isRevealed()?(e.hide(),t.setAttribute("data-open",!1)):(e.reveal(),t.setAttribute("data-open",!0))})});